import { useEffect, useState } from "react";
import "./Card.css";



function Assitido({ javisto }) {
  if (javisto) {
    return <p>Assistido ✔</p>;
  }
  return <p className="item">Não assistido</p>;
}

export default function Card() {
  const [data, setData] = useState(null);
  const options = {
    method: 'GET',
    headers: {
      'X-RapidAPI-Key': '000433B6D2BC92F482271F7175271AEDBF92A704849BB000DBA2F56DEBBE44316 9E C6 B0 D7 58 3C 52 8C F9 98 3E 68 3C 1A 6BBB 63 DD EC 9C 55 6A 52 7E 73 5C 80 84 C4 90 C1E1 4E',
      'X-RapidAPI-Host': 'my-json-server.typicode.com'
    }
  };

  useEffect(() => {
    fetch('https://my-json-server.typicode.com/marycamila184/movies/movies/', options)
    .then(response => response.json())
    .then(data =>setData(data))
    .catch(err => console.error(err))
  }, []);

  if(!data){
    return <p>Carregando...</p>
  }

  return (
    <div className="container text-center">
      <div>Filme: {data.results.map((result, index) => <div key={index}> {result.title}</div>)}</div>
    </div>
  )
}



