import React from 'react';
import { useParams } from 'react-router-dom';

const filmes = [{
    'nome': 'Vingadores',
    'duracao': '2H30',
    'ano': 2012,
    'genero': 'ação/Nerd',
},
{
    'nome': 'Vingadores2',
    'duracao': '2H00',
    'ano': 2014,
    'genero': 'ação/Nerd',
    'descricao': 'Filme da Marvel de super-heróis',
    'nota': 8.5
},
{
    'nome': 'Vingadores3',
    'duracao': '2H40',
    'ano': 2018,
    'genero': 'ação/Nerd',
    'descricao': 'Filme da Marvel de super-heróis',
    'nota': 8
}
]
function Detalhes() {
    const { filme } = useParams();

    return (
        <div>
            <p>Filme: {filme}</p>
            {(() => {
                if (filme == 'Vingadores') {
                    return (
                        <div>
                            <p>{filmes[0].nome}</p>
                            <p>{filmes[0].ano}</p>
                            <p>{filmes[0].descricao}</p>
                            <p>{filmes[0].genero}</p>
                            <p>{filmes[0].nota}</p>

                            <a href={`https://www.youtube.com/results?search_query=${filme.nome} Trailer`}>
                                <div className='btn btn-primary'>
                                    Trailer
                                </div>
                            </a>
                        </div>
                    )
                } else if (filme == 'Vingadores2') {
                    return (
                        <div>
                            <p>{filmes[1].nome}</p>
                            <p>{filmes[1].ano}</p>
                            <p>{filmes[1].descricao}</p>
                            <p>{filmes[1].genero}</p>
                            <p>{filmes[1].nota}</p>
                        </div>
                    )
                } else {
                    return (
                        <div>
                            <p>{filmes[2].nome}</p>
                            <p>{filmes[2].ano}</p>
                            <p>{filmes[2].descricao}</p>
                            <p>{filmes[2].genero}</p>
                            <p>{filmes[2].nota}</p>
                        </div>
                    )
                }
            })}
        </div>
    )
}
export default Detalhes;
