import { useEffect, useState } from "react";
import "./Card.css";

const filmes = [{
  "nome": "Vingadores",
  "duracao": "2H30",
  "foto": "Vingadores.jpg",
  "ano": 2010,
  "assistido": true,
  "genero": "Ação/Nerd",
  "descricao": "Filme da Marvel com super-heróis",
  "nota": 5
},
{
  "nome": "Vingadores 2",
  "duracao": "2H30",
  "foto": "Vingadores.jpg",
  "ano": 2014,
  "assistido": true,
  "genero": "Ação/Nerd",
  "descricao": "Filme da Marvel com super-heróis",
  "nota": 5
},
{
  "nome": "Vingadores 3",
  "duracao": "2H30",
  "foto": "Vingadores.jpg",
  "ano": 2015,
  "assistido": false,
  "genero": "Ação/Nerd",
  "descricao": "Filme da Marvel com super-heróis",
  "nota": 5
}
]

function Assitido({ javisto }) {
  if (javisto) {
    return <p>Assistido ✔</p>;
  }
  return <p className="item">Não assistido</p>;
}

export default function Card() {
  const [data, setData] = useState(null);
  useEffect(() => {
    fetch('https://my-json-server.typicode.com/marycamila184/movies/movies')
    .then(response => response.json())
    .then(data =>setData(data))
    .catch(err => console.error(err))
  }, []);
  if(!data){
    return <p>Carregando...</p>
  }

  return (
    <div className="container text-center">
      <input className="buscador" type="text" name="Busca" id="buscaFilme" placeholder="Busca por filmes aqui"/>
      <div className="row">
        {filme.map((filme, i) => (
          <div className="col" key={i}>
            <div className="card">
              <img src={'/assets/images/' + filme.foto} alt={filme.nome} className="card-img-top" />
              <div className="card-body">
                <h5 className="card-title"> </h5>
                <p>Sinopse</p>
                <p className="card-text">{filme.descricao}</p>
                <p>{filme.duracao}</p>
                <p>{filme.genero}</p>
                <p>{filme.nota}</p>
                <Assitido
                  javisto={filme.assistido}
                />
                <a
                  href={`/detalhes/${filme.nome}`}
                >
                  <div className="btn btn-primary">
                    Detalhes
                  </div>
                </a>
              </div>
            </div>

          </div>
        ))}
      </div>
    </div>
  )
}
