import { useEffect, useState } from "react";
import "./Card.css";
import Busca from "./busca";

function Assitido({ javisto }) {
  if (javisto) {
    return <p>Assistido ✔</p>;
  }
  return <p className="item">Não assistido</p>;
}

export default function Card() {
  const [data, setData] = useState(null);

  const options = {
    method: 'GET'
  };

  useEffect(() => {
    fetch('https://my-json-server.typicode.com/marycamila184/movies/movies')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(err => console.error(err))
  }, []);

  if (!data) {
    return <p>Carregando...</p>
  }

  return (
    <div className="container text-center">
      <input className="buscador" type="search" name="Busca" id="buscaFilme" value="buscando" placeholder="Busca por filmes aqui" />
      <div className="row">
        {(() =>{
          if(2 == 2){
            <Busca />
          }else{

          }
        })()}
        {data.map((filme, i) => (

          
          <div className="col" key={i}>
            <div className="card">
              {
              //Imagem
              }
              <div className="card-body">
                <h5 className="card-title">{filme.titulo} ({filme.ano}) </h5>
                <p>Nota: {filme.nota}</p>
                <Assitido
                  javisto={filme.assistido}
                />
                <a
                  href={`/detalhes/${filme.nome}`}
                >
                  <div className="btn btn-primary">
                    Detalhes
                  </div>
                </a>
              </div>
            </div>

          </div>
        ))}
      </div>
    </div>

  )
}
