import { useEffect, useState } from "react";
import "./Card.css";
import Busca from "./Busca";

function Assitido({ javisto }) {
  if (javisto) {
    return <p>Assistido ✔</p>;
  }
  return <p className="item">Não assistido</p>;
}

export default function Card() {
  const [data, setData] = useState(null);
  const [film_Tit, setFilm_Tit] = useState('');


  useEffect(() => {
    fetch('https://my-json-server.typicode.com/marycamila184/movies/movies')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(err => console.error(err))
  }, []);

  if (!data) {
    return <p>Carregando...</p>
  }

  function handleTitChange(event){
    setFilm_Tit(event.target.value);
    console.log('Em card: ', {film_Tit})
  }

  const busca = filmes.filter((movie) => 
    movie.titulo.toLowerCase().includes(film_Tit)
  );
  
  return (
    <div className="container text-center">
      <input className="buscador" type="search" name="busca" id="buscaFilme" value={film_Tit} onChange={handleTitChange} placeholder="Busca por filmes aqui" />

      <div className="row">
        {(() => {
          if (film_Tit !== '') {
            return (
              <Busca filmeTitle={film_Tit}/>
            )

          } else {
            return (
              <div>
                {data.map((filme, i) => (
                  <div className="col" key={i}>
                    <div className="card">
                      <img src={filme.poster} alt={filme.titulo} className="card-img-top" style={{height:'400px', width: '200px'}} />
                      <div className="card-body">
                        <h5 className="card-title">{filme.titulo} ({filme.ano}) </h5>
                        <p>Nota: {filme.nota}</p>
                        <Assitido
                          javisto={filme.assistido}
                        />
                        <a
                          href={`/detalhes/${filme.nome}`}
                        >
                          <div className="btn btn-primary">
                            Detalhes
                          </div>
                        </a>
                      </div>
                    </div>

                  </div>
                ))}
              </div>
            )
          }
        })()}

      </div>
    </div>

  )
}
