import React from 'react';
import { useParams } from 'react-router-dom';
import Title from '../components/Titulo';
import Comments from './../components/Comments/index';

const filmes = [{
    "nome": "Vingadores",
    "genero": "Ação/Nerd",
    "descricao": "Filme da Marvel com super-heróis",
    "nota": 5
},
{
    "nome": "Vingadores 2",
    "genero": "Ação/Nerd",
    "descricao": "Filme da Marvel com super-heróis",
    "nota": 5
},
{
    "nome": "Vingadores 3",
    "genero": "Ação/Nerd",
    "descricao": "Filme da Marvel com super-heróis",
    "nota": 5
}
]

const filmesCartaz = async(filmesName) => {
    const response = await fetch(`https://my-json-server.typicode.com/marycamila184/movies/movies`)
}
function Detalhes() {
    const { filme } = useParams();

    const filmeEscolhido = filmes.filter(f =>
        f.nome === filme
    );

    return (
        <div>
            <Title
                title={"Detalhes"}
                text="Detalhes do filme selecionado" />
            <div className="container">
                <div class='row' style={{border: '1px solid #d3d3d3'}}>
                    <div class='col-6' style={{marginLeft: '190px', width:'500px'}}>
                        <img src={'/assets/images/Vingadores.jpg'} alt={filme.nome} className="card-img-top" />
                    </div>
                    <div class='col-4'>
                        <div class='card'>
                            <div class='card-header'>
                                <p style={{textAlign:'center'}}>{filmeEscolhido[0].nome}</p>
                            </div>
                            
                            <div>
                                <p>Descrição: {filmeEscolhido[0].descricao}</p>
                                <p>Gênero: {filmeEscolhido[0].genero}</p>
                            </div>
                        </div>

                    </div>                    
                </div>
                <Comments filme={filmeEscolhido[0].nome} />
            </div>
        </div>
    )
}

export default Detalhes;
